INSERT INTO `books` VALUES (1,'Basic Cancer Center','basic-cancer-center','A basic cancer treatment center is able to provide at least one cancer treatment modality either as a stand-alone facility (chemotherapy, radiotherapy, or surgical oncology unit) or within the precincts of a hospital. In addition, it should also be able to offer prevention, screening, early detection, diagnosis, registration, treatment, palliative care, and survivorship services. It is expected that this center will be a facility at level 4 or above as per the Kenya Essential Package of Health (KEPH).','2022-07-27 11:41:45','2022-07-27 11:42:49',1,1,0,NULL,'2022-07-27 11:42:49',1,0),(2,'Basic Cancer Center','basic-cancer-center','A basic cancer treatment centre is able to provide at least one cancer treatment modality either as a stand-alone facility (chemotherapy, radiotherapy or surgical oncology unit) or within the precincts of a hospital. In addition, it should also be able to offer prevention, screening, early detection, diagnosis, registration, treatment, palliative care and survivorship services. It is expected that this centre will be a facility at level 4 or above as per the Kenya Essential Package of Health (KEPH).','2022-07-27 11:43:39','2022-07-28 08:45:13',1,1,0,NULL,'2022-07-28 08:45:13',1,0),(3,'Mid Level Cancer Center','mid-level-cancer-center','','2022-07-27 12:51:06','2022-07-27 12:51:31',1,1,0,NULL,'2022-07-27 12:51:31',1,0),(4,'National Guidelines for Establishment of Cancer Management Centers in Kenya','national-guidelines-for-establishment-of-cancer-management-centers-in-kenya','','2022-07-28 08:46:25','2022-07-29 13:41:17',1,1,0,NULL,'2022-07-29 13:41:17',1,0),(5,'Basic Cancer Center','basic-cancer-center','','2022-07-29 09:06:16','2022-08-11 10:03:41',1,1,0,4,NULL,1,0),(6,'Middle Level Cancer Center','middle-level-cancer-center','','2022-07-29 11:37:23','2022-08-11 12:10:08',1,1,0,6,NULL,1,0),(7,'Comprehensive Cancer Center','comprehensive-cancer-center','','2022-07-29 12:50:07','2022-08-11 11:33:31',1,1,0,5,NULL,1,0);
