INSERT INTO `chapters` VALUES (1,1,'basic-cancer-center','Basic Cancer Center','A basic cancer treatment centre is able to provide at least one cancer treatment modality either as a stand-alone\r\nfacility (chemotherapy, radiotherapy or surgical oncology unit) or within the precincts of a hospital. In addition, it\r\nshould also be able to offer prevention, screening, early detection, diagnosis, registration, treatment, palliative\r\ncare and survivorship services. It is expected that this centre will be a facility at level 4 or above as per the Kenya Essential Package of Health (KEPH).',2,'2022-07-15 09:27:00','2022-07-20 11:08:10',1,1,0,'2022-07-20 11:08:10',1),(2,1,'middle-level-cancer-center','Middle Level Cancer Center','',3,'2022-07-18 09:31:38','2022-07-20 11:08:10',1,1,0,'2022-07-20 11:08:10',1),(3,1,'comprehensive-cancer-center','Comprehensive Cancer Center','',4,'2022-07-18 12:01:45','2022-07-20 11:08:10',1,1,0,'2022-07-20 11:08:10',1),(4,2,'ewew','EWEW','EWE',4,'2022-07-20 11:59:32','2022-07-20 12:02:18',1,1,0,'2022-07-20 12:02:18',1),(5,2,'c1','C1','WDQ',2,'2022-07-20 12:02:44','2022-07-20 12:03:38',1,1,0,'2022-07-20 12:03:38',1),(6,4,'c1','C1','C2',2,'2022-07-20 12:05:19','2022-07-20 12:06:42',1,1,0,'2022-07-20 12:06:42',1),(7,4,'basic-cancer-center','Basic Cancer Center','A basic cancer treatment center is able to provide at least one cancer treatment modality either as a stand-alone facility (chemotherapy, radiotherapy, or surgical oncology unit) or within the precincts of a hospital. In addition, it should also be able to offer prevention, screening, early detection, diagnosis, registration, treatment, palliative care, and survivorship services. It is expected that this center will be a facility at level 4 or above as per the Kenya Essential Package of Health (KEPH).',6,'2022-07-20 13:15:14','2022-07-20 13:15:14',1,1,0,NULL,1),(8,4,'middle-level-cancer-center','Middle Level Cancer Center','',7,'2022-07-21 10:19:29','2022-07-21 10:19:29',1,1,0,NULL,1),(9,4,'comprehensive-cancer-center','Comprehensive Cancer Center','',8,'2022-07-21 17:00:04','2022-07-21 17:00:04',1,1,0,NULL,1);
